viewof sliderRaw = Scrubber(d3.range(numberOfDays + 1), {
  delay: 1000,
  loop: false,
  autoplay: false,
  initial: defaultDateIndex
})

viewof slider = {
  const element = html`0.9s slider debounce delay`
  element.value = viewof sliderRaw.value
  const debounced = debounce(viewof sliderRaw, 900)
  async function run () {
    for await (const value of debounced) {
      element.value = value
      element.dispatchEvent(new Event("input", {bubbles: true}))
    }
  }
  run()
  return element
}

